PAGE.addWait(
 "Modules.demo"
 , [
  "Constructors.LocalStorage"
 ]
 , function(ref) {

 var dog = {
  $html : $("#upsell")
  , sitePreferences : ref.LocalStorage("SitePreferences")
 }

 function events() {
  dog.$html.find(".kill").click(function() {
   dog.sitePreferences.addSaveProperty("HideQBLUpsell", true)
   dog.$html.fadeOut()
  })
 }

 function init() {

  if (!PAGE.exists("data.HideQBLUpsell", dog.sitePreferences)) {
   events()
   dog.$html.show()
  } else {
   dog.$html.hide()
  }
 }

 init()

 return dog

})

